import { Component, inject, OnInit } from '@angular/core';
import { orderBy } from '@angular/fire/firestore';
import { LoadingController, ModalController, NavController, Platform } from '@ionic/angular';
import { Inversion_Productos } from 'src/app/models/inversion_productos.model';
import { FirebaseService } from 'src/app/services/firebase.service';
import { UtilsService } from 'src/app/services/utils.service';
import { AddUpdateInversionComponent } from 'src/app/shared/components/add-update-inversion/add-update-inversion.component';
import * as pdfMake from 'pdfmake/build/pdfmake';
import * as pdfFonts from 'pdfmake/build/vfs_fonts';
import { Directory, Filesystem } from '@capacitor/filesystem';
import { Share } from '@capacitor/share';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-visualizer-products',
  templateUrl: './visualizer-products.page.html',
  styleUrls: ['./visualizer-products.page.scss'],
})
export class VisualizerProductsPage implements OnInit {

  firebaseSvc = inject(FirebaseService);
  utilsSvc = inject(UtilsService);
  navCrtl = inject(NavController); // Inyecta ModalController
  products: Inversion_Productos[] = [];
  imageLoaded: boolean[] = [];
  searchTerm: string = '';
  loading: boolean = false;




  constructor(private loadingController: LoadingController,
    private platform: Platform) { }

  ngOnInit() {
    this.getProducts();
  }

  ionViewWillEnter() {
    this.getProducts();
  }


  doRefresh(event) {

    setTimeout(() => {
      this.getProducts();
      event.target.complete();
    }, 2000);
  }


  ///=====OBTENER PRODUCTOS=====////

  getProducts() {
    let path = `inversion_productos`;

    this.loading = true;

    const query = orderBy('nombre', 'asc');

    let sub = this.firebaseSvc.getCollectionData(path, query).subscribe({
      next: (res: Inversion_Productos[]) => {
        console.log(res);
        this.products = res;
        this.loading = false;
        sub.unsubscribe();
      }
    })
  }

  filterProducts() {
    const term = this.searchTerm.toLowerCase();

    // Filtra los productos en función del término de búsqueda
    this.products = this.products.filter(product =>
      product.nombre.toLowerCase().includes(term) ||
      product.tipo_unidad.toLowerCase().includes(term) ||
      product.precio_unidad.toString().includes(term)
    );
  }

  close() {
    this.navCrtl.back();
  }

  ///=====CONFIRMAR ELIMINACIÓN=====///
  async DeleteProduct(product: Inversion_Productos) {
    // Confirmación antes de eliminar el producto
    this.utilsSvc.presentAlert({
      header: 'Eliminar Producto',
      message: '¿Estás seguro de que deseas eliminar este producto?',
      mode: 'ios',
      buttons: [
        {
          text: 'Cancelar',
        },
        {
          text: 'Eliminar',
          handler: () => {
            this.delete(product); // Llama a la función delete para eliminar el producto
          }
        }
      ]
    });
  }

  ///=====ELIMINAR EL PRODUCTO=====///
  async delete(product: Inversion_Productos) {
    let path = `inversion_productos/${product.id}`; // Ruta del documento en Firestore

    const loading = await this.utilsSvc.loading();
    await loading.present();

    try {
      // Primero, elimina la imagen del producto si tiene una asociada
      if (product.image) {
        let imagePath = await this.firebaseSvc.getFilePath(product.image);
        if (imagePath) {
          await this.firebaseSvc.deleteFile(imagePath); // Eliminar la imagen de Firebase Storage
        }
      }

      // Luego, elimina el documento del producto de Firestore
      await this.firebaseSvc.deleteDocument(path);

      // Actualiza la lista local de productos
      this.products = this.products.filter(p => p.id !== product.id);

      // Muestra un mensaje de éxito
      this.utilsSvc.presentToast({
        message: 'Producto eliminado correctamente',
        duration: 4500,
        color: 'success',
        position: 'middle',
        icon: 'checkmark-circle-outline'
      });

    } catch (error) {
      console.log(error);

      // En caso de error, muestra un mensaje
      this.utilsSvc.presentToast({
        message: error.message,
        duration: 4500,
        color: 'danger',
        position: 'middle',
        icon: 'alert-circle-outline'
      });
    } finally {
      // Oculta el loading después de completar la operación
      loading.dismiss();
    }
  }
  //ACTUALIZAR PRODUCTO

  async addUpdateProduct(products?: Inversion_Productos) {

    let success = await this.utilsSvc.presentModal({
      component: AddUpdateInversionComponent,
      cssClass: 'add-update-modal',
      componentProps: { products }
    })

    if (success) this.getProducts()
  }



  makePDF() {
    const totalBolivares = this.products.reduce((sum, product) => sum + Number(product.valor_total_bolivares), 0).toFixed(2);
    const totalDolares = this.products.reduce((sum, product) => sum + Number(product.valor_total_dolares), 0).toFixed(2);



    const documentDefinition = {
      header: {
        margin: [20, 0, 0, 0],
        columns: [
          {
            image: 'data:image/png;base64,',

            width: 150,
            margin: [5, 10, 0, 0]
          }
        ]

      },
      content: [
        { text: 'Listado de Productos', style: 'header', alignment: 'center' },
        {
          table: {
            headerRows: 1,
            widths: ['*', 'auto', 'auto', 'auto', 'auto'],
            body: [
              ['Nombre del Producto', 'Cantidad Disponible', 'Costo Unitario', 'Costo Total En $', 'Costo Total En Bs'],
              ...this.products.map(product => [
                product.nombre,
                product.medida + ' ' + product.tipo_unidad,
                Number(product.precio_unidad),
                Number(product.valor_total_dolares).toFixed(2),
                Number(product.valor_total_bolivares).toFixed(2)
              ])
            ]
          }
        },
        { text: `\nTotal en Bolívares: ${totalBolivares} Bs`, bold: true, alignment: 'right' },
        { text: `\nTotal en Dólares: ${totalDolares} $`, bold: true, alignment: 'right' }
      ],
      styles: {
        header: {
          fontSize: 22,
          bold: true,
          margin: [0, 0, 0, 10]
        },
        tableHeader: {
          bold: true,
          fontSize: 13,
          color: 'black'
        }
      }
    };

    const pdfDocGenerator = pdfMake.createPdf(documentDefinition);
    pdfDocGenerator.getBase64(async (base64) => {
      //let base64 = canvas.toDataURL();
      let path = 'Reporte De Productos ' + '.pdf';


      const loading = await this.loadingController.create({ spinner: 'crescent' });
      await loading.present();


      await Filesystem.writeFile({
        path,
        data: base64,
        directory: Directory.Cache,
      }).then(async (res) => {

        let uri = res.uri;

        await Share.share({ url: uri });

        await Filesystem.deleteFile({
          path,
          directory: Directory.Cache
        })
      }).finally(() => {

        loading.dismiss();
      });
    });

    pdfMake.createPdf(documentDefinition).open();
  }


}
